package com.example;

public class Stacking {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		try
		{int a[]= new int[1];
		a[0]=21;
		a[1]=22;
		
		System.out.println(a);
		}
		catch(Exception a)
		{
			System.out.println("Can'nt store more then 1 number in the array");
		}
		
		int b[]= new int[1];
		b[0]=21;
		
		System.out.println(b);
	
	}

}
